var assert = require('assert');
var accessorize = require('../accessorizer');

suite('The basic stuff', function basiSuite() {
  test('basic accessorize', 
    function testBasicAccessor(testDone) {
      var accessor = accessorize.createBasicAccessor('id');
      assert.equal(typeof accessor, 'function');

      var objectWithId = {
        id: 'fhqwhgads',
        otherstuff: [1, 2, 3]
      };

      var objectWithId2 = {
        id: 'tapeleg',
        otherstuff: [1, 2, 3]
      };

      assert.equal(accessor(objectWithId), 'fhqwhgads');
      assert.equal(accessor(objectWithId2), 'tapeleg');

      testDone();
    }
  );
});
